/*Table structure for table `ims_message` */

DROP TABLE IF EXISTS `ims_message`;

CREATE TABLE `ims_message` (
  `message_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `type` tinyint(1) NOT NULL COMMENT '消息类型(自定义)',
  `member` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0=私有函 1=顾客组 2=管理组',
  `title` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '标题',
  `content` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '内容',
  `url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '外部链接',
  `target` enum('_self','_blank') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '_self' COMMENT '_self _blank',
  `page_views` int(11) NOT NULL DEFAULT '0' COMMENT '游览量',
  `is_top` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否置顶 0=否 1=是',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0=禁用 1=启用',
  `is_delete` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0=未删 1=已删',
  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建日期',
  `update_time` int(11) NOT NULL DEFAULT '0' COMMENT '更新日期',
  PRIMARY KEY (`message_id`),
  KEY `type` (`type`),
  KEY `is_top` (`is_top`),
  KEY `status` (`status`),
  KEY `is_delete` (`is_delete`),
  KEY `member` (`member`) USING BTREE,
  KEY `create_time` (`create_time`)
