/*Table structure for table `ims_payment_log` */

DROP TABLE IF EXISTS `ims_payment_log`;

CREATE TABLE `ims_payment_log` (
  `payment_log_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `payment_no` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '流水号',
  `out_trade_no` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '交易号',
  `order_no` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '订单号',
  `user_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '对应user表',
  `amount` decimal(10,2) NOT NULL COMMENT '支付金额',
  `payment_time` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '支付日期',
  `to_payment` varchar(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '支付方式',
  `type` tinyint(1) NOT NULL COMMENT '0=充值 1=订单',
  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建日期',
  `update_time` int(11) NOT NULL DEFAULT '0' COMMENT '更新日期',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0=待付款 1=已完成 2=已关闭',
  PRIMARY KEY (`payment_log_id`),
  KEY `payment_no` (`payment_no`),
  KEY `user_id` (`user_id`),
  KEY `order_no` (`order_no`),
  KEY `type` (`type`),
  KEY `status` (`status`),
  KEY `trade_no` (`out_trade_no`)
) ENGINE=InnoDB AUTO_INCREMENT=183 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='支付日志';

